import { useEffect, useState } from "react"
import { getGifs } from "../helpers/getGifs";

export const useFetchGifs = (category) => {
  const [state, setstate] = useState({
      data: [],
      loading: true
  });
  // useEffect( () => {
  //   getGifs(category)
  //   .then(imgs => setimages(imgs))
  // }, [category]);
  useEffect(() => {
    getGifs(category)
    .then(img => {
        setTimeout(() => {
            setstate({
                data: img,
                loading: false
            });
        },3000)

    })
  }, [category])
  return state; //{data: [], loading:true}
}
