import React from 'react'

export const GifGrid = ({category}) => {

  const getGifs = async() => {
      const url = 'https://api.giphy.com/v1/gifs/search?q=Rick+and+mory&limit=10&api_key=C6ftLwqcf5XVjZnsLKmmfefyz4Axk8Hz';
      const resp = await fetch(url);
      const {data} = await resp.json();

      const  gifs =data.map(img => {
          return {
              id: img.id,
              title: img.title,
              url: img.image.downsized_medium.url
          }
      })
  }
  console.log(gifs)
  return (
    <div>
        {
            data.map(gif => <img src={gif.url}/>)
        }
        <h3>{category}</h3>
    </div>
  )
}
